mapper.select:
select count(id) from blog_comment


        and module_id = #{commentModule.id,jdbcType=INTEGER}
        and module_name = #{commentModule.name,jdbcType=VARCHAR}

select bc1.id, bc1.parent_id, bc1.path, bc1.nickname, bc1.email, bc1.ip, bc1.admin, bc1.content, bc1.module_id, bc1.create_at, bc1.modify_at, bc1.website, bc1.avatar, bc1.module_name, bc1.checking, bc2.id as parent_id, bc2.content as parent_conent, bc2.nickname as parent_name
from blog_comment bc1 left join blog_comment bc2 on bc1.parent_id = bc2.id
and bc1.module_id = #{commentModule.id,jdbcType=INTEGER} and bc1.module_name = #{commentModule.name,jdbcType=VARCHAR}
order by bc1.create_at desc
limit #{start}, #{offset}
